import React, { useState } from "react";

interface ButtonFieldProps {
  onClick: () => void;
  isSignUp: boolean;
}

const ButtonField: React.FC<ButtonFieldProps> = ({ onClick, isSignUp }) => {
  const [rightButtonSelected, setRightButtonSelected] = useState(false);

  const handleRightButtonClick = () => {
    setRightButtonSelected(true);
  };

  return (
    <div
      className={`input-field-container ${
        rightButtonSelected ? "right-selected" : ""
      }`}
    >
      <button onClick={onClick} className="input-button left-button">
        {isSignUp ? "Switch to Login" : "Switch to Sign Up"}
      </button>

      <button
        className="input-button right-button"
        onClick={handleRightButtonClick}
      ></button>
    </div>
  );
};

export default ButtonField;
