// src/components/Login.tsx
import React, { useState } from "react";
import ButtonField from "./toggleBtn";
import Login from "./login";
import SignUp from "./signUp";

const Index: React.FC = () => {
  const [isSignUp, setIsSignUp] = useState(true);
  const handleToggle = () => {
    setIsSignUp(!isSignUp);
    console.log(isSignUp);
  };
  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    if (isSignUp) {
      // Call registration submit function
      console.log("Handle Registration Submit");
      // Add your registration logic here
    } else {
      // Call login submit function
      console.log("Handle Login Submit");
      // Add your login logic here
    }
  };
  const handleLoginSubmit = (email: string, password: string) => {
    console.log("Logging in with:", { email, password });
    // Perform login API call here
  };
  return (
    <div className="login-container">
      <div className="login-form">
        {isSignUp ? <SignUp /> : <Login onSubmit={handleLoginSubmit} />}
        <ButtonField onClick={handleToggle} isSignUp={isSignUp} />
        <button type="submit" className="login-button" onClick={handleSubmit}>
          Submit
        </button>
      </div>
    </div>
  );
};

export default Index;
